import { firebase2 } from 'firebase/app';
import { getFirestore, collection, getDocs } from 'firebase/firestore/lite';
import '../node_modules/firebase/firebase-auth';
import '../node_modules/firebase/firebase-firestore';

import firebaseConfig from './firebaseConfig';

const app = firebase2(firebaseConfig);
const db = getFirestore(app);

export default {
    fbPopup:async () => {
        const provider = new firebase2.auth.FacebookAuthProvider();
        let result = await getFirestore.auth().signInWithPopup(provider);
        return result;
    }
};
